import React, { Component } from 'react';
import './App.css';

import Person from './Person/Person';
import Listener from './Listener/Listener';
import Validation from './Validation/Validation';
import CharComponent from './CharComponent/CharComponent';

class App extends Component {
  state = {
    people: [
      {name: "ALI", age: 24},
      {name: "Ahmed", age: 34},
      {name: "Faiz",age: 46}

    ],
    showPersons: false,
    len:0,
    typed:'',
    word:[
      {len:0,typed:'',letter:''}
    ]
  }

  //practice as event handler
deletePersonHandler = (personIndex) =>{
  //const pers = this.state.people.slice();/create copy then chnage state
  const pers = [...this.state.people];
  pers.splice(personIndex,1);
  this.setState({people: pers});

}

stringChangeHandler = (event)=>{
  const words = [...this.state.word]
  words.typed = event.target.value;
  words.len = (event.target.value).length
  this.setState({
    word: words
  })

}

  nameChangeHandler = (event,id) =>{
    const peopleIndex = this.state.people.findIndex(p => {
      return p.id ===id;
    });

    console.log(peopleIndex);

    const person ={...this.state.people[peopleIndex]};

    person.name = event.target.value;

    const peeps = [...this.state.people];
    peeps[peopleIndex] = person;

    this.setState({
      people: peeps
    }
    )

  }

  togglePersonHandler = () =>{
    const doShow = this.state.showPersons;
    console.log(doShow);
    this.setState({showPersons: !doShow});


  }


  render() {
    const style = {
      backgroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'

    };

    let persons,lent,words = null;

    if (this.state.showPersons) {
      persons = (
        <div>
          {this.state.people.map((person,index)=>{
            return <Person 
            key={person.id}
            name = {person.name} 
            age = {person.age}
            click={()=>this.deletePersonHandler(index)}
            change={(event)=>this.nameChangeHandler(event,person.id)}
            />
          })}

        {/* <Person 
        name={this.state.people[0].name} 
        age ={this.state.people[0].age}/>
        <Person 
        name={this.state.people[1].name} 
        age ={this.state.people[1].age}
        click = {this.switchNameHandler.bind(this,'AAAAA111123123')}
        changed = {this.nameChangeHandler}>My Hobbies: Racing</Person>
        <Person 
        name={this.state.people[2].name} 
        age ={this.state.people[2].age}/> */}

        </div> 
      )
    }

    if (this.state.word.len <5) {
      lent = (
      <div>
        <Validation message = 'string too short'/>

        <div>
        {this.state.word.map((words,index)=>{
            return <CharComponent 
              letter= {words}
            />
          })}

        </div>


      </div>
      )
    } 
    else {
      lent = (
      <Validation message = 'string length OK!'/>
      )
    } 


    return (
      //all html to be wrapped in one root div
      <div className="App">

          <h1>Hi New react App</h1>
          <p>This is really working</p>
          <button style={style} onClick={this.togglePersonHandler}>Switch Name</button>

          {persons}

          <Listener
          len = {this.state.word.len}
          changed = {this.stringChangeHandler}/>
          {lent}







      </div>

    );

  }
}

export default App;
