import React, { Component } from 'react';
//import React, {useState}
import './App.css';
//import UserInput from './UserInput/UserInput';
//import UserOutput from './UserOutput/UserOutput';
import Person from './Person/Person';
//props sent from outside a component
//state is to
class App extends Component {
  state = {
    people: [
      {name: "ALI", age: 24},
      {name: "Ahmed", age: 34},
      {name: "Faiz",age: 46}

    ],
    username: 'Fraz',
    showPersons: false
  }

  //practice as event handler
  switchNameHandler = (NewName) =>{
    //Dont do do that it will cause errors this.state.people[0].name = "Something"
    this.setState({ people : [
      {name: NewName, age: 224},
      {name: "Abbbb", age: 314},
      {name: "Fcccc",age: 416}
    ]   
    })
  }

  nameChangeHandler = (event) =>{
    this.setState({
      people: [
        {name: "ALI", age: 24},
        {name: event.target.value, age: 34},
        {name: "Faiz",age: 46}

      ]
    }
    )

  }

  togglePersonHandler = () =>{
    const doShow = this.showPersons;
    this.setState({showPersons: !doShow});
    console.log(doShow);

  }


  render() {
    const style = {
      backgroundColor: 'white',
      font: 'inherit',
      border: '1px solid blue',
      padding: '8px',
      cursor: 'pointer'

    };
    return (
      //all html to be wrapped in one root div
      <div className="App">

          <h1>Hi New react App</h1>
          <p>This is really working</p>
          <button style={style} onClick = {this.togglePersonHandler}>Switch Name</button>

        { this.state.showPersons === true ?
          <div >
            <Person 
            name={this.state.people[0].name} 
            age ={this.state.people[0].age}/>
            <Person 
            name={this.state.people[1].name} 
            age ={this.state.people[1].age}
            click = {this.switchNameHandler.bind(this,'AAAAA111123123')}
            changed = {this.nameChangeHandler}>My Hobbies: Racing</Person>
            <Person 
            name={this.state.people[2].name} 
            age ={this.state.people[2].age}/> 
        </div> : null
        }
        {/* <UserInput username = {this.state.username} change= {this.userChangeHandler}/>
        <UserOutput username = {this.state.username}/> */}



      </div>

    );
    //cannot use word "class" use "className" instead

    // createElement(Tag, configuration , content)
    //return React.createElement('div',{className:'App'},React.createElement('h1',null,'Hi New react App!!'));
  }
}

export default App;
