import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';
//props sent from outside a component
//state is to
class App extends Component {
  state = {
    people: [
      {name: "ALI", age: 24},
      {name: "Ahmed", age: 34},
      {name: "Faiz",age: 46}

    ]
  }
  render() {
    return (
      //all html to be wrapped in one root div
      <div className="App">
        <h1>Hi New react App</h1>
        <p>This is really working</p>
        <button>Switch Name</button>
        <Person name={this.state[0].name} age ={this.state[0].age}/>
        <Person name={this.state[1].name} age ={this.state[1].age}>My Hobbies: Racing</Person>
        <Person name={this.state[2].name} age ={this.state[2].age}/>
      </div>

    );
    //cannot use word "class" use "className" instead

    // createElement(Tag, configuration , content)
    //return React.createElement('div',{className:'App'},React.createElement('h1',null,'Hi New react App!!'));
  }
}

export default App;
